import React from 'react';
import CountUp from 'react-countup';
import styles from './TrainingStats.module.css';

const StudentsTrained = () => {
  return (
    <section className="relative flex flex-col md:flex-row items-center justify-around bg-gray-900 text-white p-12 shadow-xl overflow-hidden">
      {/* Background Animation */}
      <div className="absolute inset-0 z-0 bg-gradient-to-r from-red-500 to-pink-500 animate-pulse opacity-30" />

      {/* Content */}
      <div className="relative z-10 flex-1 text-center md:text-left">
        <h2 className="text-4xl font-extrabold mb-4">Empowering Future Leaders</h2>
        
        {/* Description */}
        <p className="text-lg mb-6">
          Over the years, I have had the privilege of training passionate individuals in the field of [Your Expertise].
          My sessions focus on hands-on learning, practical skills, and real-world applications to ensure that every student is prepared to excel.
        </p>
        
        {/* Key Highlights */}
        <ul className="list-disc list-inside mb-6 space-y-2">
          <li>Comprehensive curriculum covering [Topics Covered].</li>
          <li>Real-world projects and case studies.</li>
          <li>Personalized mentorship and guidance.</li>
          <li>Certificate of completion for every participant.</li>
        </ul>

        {/* Call to Action */}
        <a href="#contact" className="inline-block px-6 py-3 bg-red-500 text-white font-bold rounded-lg shadow-lg hover:bg-red-600 transition">
          Join the Next Batch
        </a>
      </div>

      {/* Glowing Counter */}
      <div className="relative z-10 flex-1 mt-8 md:mt-0 text-center">
        <h3 className="text-3xl font-bold mb-2">Students Trained</h3>
        <div className={`text-6xl font-extrabold text-red-500 ${styles.glow}`}>
  <CountUp
    start={0}
    end={500}  // Replace 500 with your actual number
    duration={4}  // Duration of the animation in seconds
    separator=","
  />
</div>
        <p className="text-lg mt-4">and counting...</p>
      </div>
    </section>
  );
};

export default StudentsTrained;
