import React from 'react';
import next from '../assets/next.png';

const TechNews = () => {
  return (
    <section className="relative flex flex-col md:flex-row items-start justify-between bg-gray-100 p-8 md:p-12 shadow-lg">
      {/* Images Section */}
      <div className="relative z-10 flex flex-col items-center md:items-start space-y-6 md:space-y-8 w-full md:w-1/2">
        {/* Image 1 - Top */}
        <div className="relative w-full max-w-xs h-40 rounded-lg overflow-hidden shadow-lg md:ml-20">
          <img 
            src={next} 
            alt="Tech News 1" 
            className="w-full h-full object-cover" 
          />
        </div>

        <div className="flex flex-col md:flex-row gap-6 items-center md:ml-9 sm:gap-6 sm:mb-">
          {/* Image 2 - Slightly Lower */}
          <div className="relative w-full max-w-xs h-40 rounded-lg overflow-hidden shadow-lg -mt-8 md:mt-0">
            <img 
              src={next} 
              alt="Tech News 2" 
              className="w-full h-full object-cover" 
            />
          </div>

          {/* Image 3 - Towards Left */}
          <div className="relative w-full max-w-xs h-40 rounded-lg overflow-hidden shadow-lg md:ml-6 -mt-8 md:mt-0">
            <img 
              src={next}
              alt="Tech News 3" 
              className="w-full h-full object-cover" 
            />
          </div>
        </div>
      </div>

      {/* Content Section */}
      <div className="relative z-10 flex-1 mt-8 md:mt-0 text-center md:text-left md:ml-8">
        <h2 className="text-3xl md:text-4xl font-bold mb-4">Latest Tech News</h2>
        <p className="text-lg mb-6">
          Stay updated with the latest developments in technology. From cutting-edge innovations to trends in the tech world, we bring you the news that matters.
        </p>
        <p className="text-md mb-4">
          <strong>Tech News 1:</strong> Explore the recent advancements in AI and machine learning.
        </p>
        <p className="text-md mb-4">
          <strong>Tech News 2:</strong> Discover how blockchain technology is reshaping industries.
        </p>
        <p className="text-md">
          <strong>Tech News 3:</strong> Find out about the latest gadgets and their impact on our daily lives.
        </p>
      </div>
    </section>
  );
};

export default TechNews;
