import React from 'react';
import nest from '../assets/nest.png';
import next from '../assets/next.png'

const TechNews = () => {
  return (
    <section className="relative flex flex-col md:flex-row items-center justify-around bg-gray-100 p-12 shadow-lg">
      {/* Images Section */}
      <div className="relative z-10 flex-1 flex flex-wrap justify-center items-center space-x-4 md:space-x-0">
        {/* Image 1 - Circle */}
        <div className="w-32 h-32 mb-4 md:mb-0 rounded-full overflow-hidden shadow-lg">
          <img src={next} alt="Tech News 1" className="w-full h-full object-cover" />
        </div>
        {/* Image 2 - Triangle */}
        <div className="w-0 h-0 mb-4 md:mb-0 border-l-[60px] border-r-[60px] border-b-[104px] border-transparent border-b-blue-500 overflow-hidden shadow-lg transform rotate-45">
          <img src="https://via.placeholder.com/150" alt="Tech News 2" className="w-full h-full object-cover transform -rotate-45" />
        </div>
        {/* Image 3 - Rectangle */}
        <div className="w-32 h-50 mb-4 md:mb-0 overflow-hidden shadow-lg">
          <img src={nest} alt="Tech News 3" className="w-full h-full object-cover" />
        </div>
      </div>

      {/* Content Section */}
      <div className="relative z-10 flex-1 text-center md:text-left ml-0 md:ml-10">
        <h2 className="text-4xl font-bold mb-4">Latest Tech News</h2>
        <p className="text-lg mb-6">
          Stay updated with the latest developments in technology. From cutting-edge innovations to trends in the tech world, we bring you the news that matters.
        </p>
        <p className="text-md mb-4">
          <strong>Tech News 1:</strong> Explore the recent advancements in AI and machine learning.
        </p>
        <p className="text-md mb-4">
          <strong>Tech News 2:</strong> Discover how blockchain technology is reshaping industries.
        </p>
        <p className="text-md">
          <strong>Tech News 3:</strong> Find out about the latest gadgets and their impact on our daily lives.
        </p>
      </div>
    </section>
  );
};

export default TechNews;
