import React from 'react';
import CountUp from 'react-countup';

const StudentsTrained = () => {
  return (
    <div className="relative flex flex-col items-center justify-center bg-gray-900 text-white p-10 rounded-lg shadow-lg overflow-hidden">
      {/* Background Animation */}
      <div className="absolute inset-0 z-0 bg-gradient-to-r from-red-500 to-pink-500 animate-pulse opacity-30" />
      
      {/* Content */}
      <div className="relative z-10 text-center">
        <h2 className="text-4xl font-extrabold mb-4">Students Trained</h2>
        
        {/* Glowing Number */}
        <div className="text-6xl font-extrabold text-red-500 glow">
          <CountUp
            start={0}
            end={500}  // Replace 500 with your actual number
            duration={4}  // Duration of the animation in seconds
            separator=","
          />
        </div>
        
        <p className="text-xl mt-4">and counting...</p>
      </div>
    </div>
  );
};

export default StudentsTrained;
